import React, { useState } from 'react';

function SearchBar() {
  const [song, setSong] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('/predict', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ song }),
    })
      .then(response => response.json())
      .then(data => {
        // Handle response data
        console.log(data);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Enter a song:
        <input type="text" value={song} onChange={(e) => setSong(e.target.value)} />
      </label>
      <button type="submit">Search</button>
    </form>
  );
}

function App() {
	// usestate for setting a javascript
	// object for storing and using data
	const [data, setdata] = useState( ['https://i.scdn.co/image/ab67616d0000b273bbc280510fa8d8c4e30f3b19',
   'https://i.scdn.co/image/ab67616d0000b273c5545f737b16ad5ee767b62a',
   'https://i.scdn.co/image/ab67616d0000b273ce1f4349305971b5beb75fcd'])
	// Using useEffect for single rendering
	useEffect(() => {
		// Using fetch to fetch the api from
		// flask server it will be redirected to proxy
		fetch("/predict").then((res) =>
			res.json().then((data) => {
				// Setting a data from api
			})
		);
	}, []);

	return (
		<div className="App">
			<header className="App-header">
				{/* Calling a data from setdata for showing */}
        {data.map((item) => (
          <img src={item} alt="img" />
        ))
          }
			</header>
		</div>
	);
}

export default SearchBar;
